#region Imports

using ReaLTaiizor.Util;
using System;
using System.ComponentModel;
using System.Drawing;
using System.Drawing.Drawing2D;
using System.Windows.Forms;

#endregion

namespace ReaLTaiizor.Controls
{
    #region AirCheckBox

    [DefaultEventAttribute("CheckedChanged")]
    public class AirCheckBox : AirControl
    {
        public AirCheckBox()
        {
            LockHeight = 17;
            Font = new("Segoe UI", 9);
            SetColor("Gradient top normal", 237, 237, 237);
            SetColor("Gradient top over", 242, 242, 242);
            SetColor("Gradient top down", 235, 235, 235);
            SetColor("Gradient bottom normal", 230, 230, 230);
            SetColor("Gradient bottom over", 235, 235, 235);
            SetColor("Gradient bottom down", 223, 223, 223);
            SetColor("Border", 167, 167, 167);
            SetColor("Text", 60, 60, 60);
            Width = 92;
            Cursor = Cursors.Hand;
        }

        private int X;
        private Color GTN, GTO, GTD, GBN, GBO, GBD, Bo, T;

        protected override void ColorHook()
        {
            GTN = GetColor("Gradient top normal");
            GTO = GetColor("Gradient top over");
            GTD = GetColor("Gradient top down");
            GBN = GetColor("Gradient bottom normal");
            GBO = GetColor("Gradient bottom over");
            GBD = GetColor("Gradient bottom down");
            Bo = GetColor("Border");
            T = GetColor("Text");
        }

        protected override void OnMouseMove(MouseEventArgs e)
        {
            base.OnMouseMove(e);
            X = e.Location.X;
            Invalidate();
        }

        protected override void PaintHook()
        {
            G.Clear(BackColor);
            LinearGradientBrush LGB = default;
            G.SmoothingMode = SmoothingMode.HighQuality;
            LGB = State switch
            {
                MouseStateAir.None => new LinearGradientBrush(new Rectangle(0, 0, 14, 14), GTN, GBN, 90f),
                MouseStateAir.Over => new LinearGradientBrush(new Rectangle(0, 0, 14, 14), GTO, GBO, 90f),
                _ => new LinearGradientBrush(new Rectangle(0, 0, 14, 14), GTD, GBD, 90f),
            };
            GraphicsPath buttonpath = CreateRound(Rectangle.Round(LGB.Rectangle), 5);
            G.FillPath(LGB, CreateRound(Rectangle.Round(LGB.Rectangle), 3));
            G.SetClip(buttonpath);
            LGB = new(new Rectangle(0, 0, 14, 5), Color.FromArgb(150, Color.White), Color.Transparent, 90f);
            G.FillRectangle(LGB, Rectangle.Round(LGB.Rectangle));
            G.ResetClip();
            G.DrawPath(new(Bo), buttonpath);

            DrawText(new SolidBrush(T), 17, -2);

            if (Checked)
            {
                Image check = Image.FromStream(new System.IO.MemoryStream(Convert.FromBase64String("")));
                G.DrawImage(check, new Rectangle(2, 3, check.Width, check.Height));
            }
        }

        private bool _Checked;
        public bool Checked
        {
            get => _Checked;
            set
            {
                _Checked = value;
                CheckedChanged?.Invoke(this);
                Invalidate();
            }
        }

        protected override void OnMouseDown(MouseEventArgs e)
        {
            Checked = !Checked;
            base.OnMouseDown(e);
        }

        public event CheckedChangedEventHandler CheckedChanged;
        public delegate void CheckedChangedEventHandler(object sender);

    }

    #endregion
}